    QWidget(parent), ui(new Ui::GWindow) {
    ui->setupUi(this);
}
//===============================================</xmp></pre></div><br><h3 class="Title8 GTitle3">Ajouter du style à la fenêtre</h3><div class="GCode1"><pre class="Code2"><xmp class="AceCode" data-mode="c_cpp">//================================================
void GStyle::load(const string& file) {
    QFile lFile(QString::fromStdString(file));
    lFile.open(QFile::ReadOnly);
    QString lStyle(lFile.readAll());
    lFile.close();
    qApp->setStyleSheet(lStyle);
}
//================================================</xmp></pre></div><br><h3 class="Title8 GTitle3">Définir le style</h3><div class="GCode1"><pre class="Code2"><xmp class="AceCode" data-mode="css">/*-----------------------------------------------*/
