lWindow->setWindowTitle("Qt | ReadyDev");

QSpinBox* lSpinBox = new QSpinBox;
lSpinBox->setRange(0, 130);

QSlider* lSlider = new QSlider(Qt::Horizontal);
lSlider->setRange(0, 130);

connect(lSpinBox, SIGNAL(valueChanged(int)), lSlider, SLOT(setValue(int)));
connect(lSlider, SIGNAL(valueChanged(int)), lSpinBox, SLOT(setValue(int)));

lSpinBox->setValue(35);

QHBoxLayout* lLayout = new QHBoxLayout;
lLayout->addWidget(lSpinBox);
lLayout->addWidget(lSlider);

lWindow->setLayout(lLayout);
lWindow->show();</xmp></pre></div><br><h3 class="Title8 GTitle3">Résultat</h3><div class="Img3 GImage"><img src="img/Layout.png" alt="img/Layout.png"></div></div></div></div></div><br><div class="Content2 GTitle1"><div class="MainBlock2"><div class="Content"><h1 class="Title2 Center" id="Gérer les boîtes de dialogue"><a class="Link3" href="#">Gérer les boîtes de dialogue</a></h1><div class="Body3">Le but de cette section est de vous apprendre à <span class="GColor1" style="color:lime;">gérer les boîtes de dialogue</span> avec Qt.<br>Produit par <b>Gérard KESSE</b>.<br><br>Une boîte dialogue permet d'afficher une fenêtre modale.<br><br><div class="Content0 GSummary2"><div class="Body0" id="Loader_1533500991485"><div class="Row26">Summary 2</div></div><script>loadSummary2("Loader_1533500991485");</script></div><br><h2 class="Title7 GTitle2" id="Gérer les boîtes de dialogue-Gérer les boîtes de dialogue"><a class="Link9" href="#Gérer les boîtes de dialogue">Gérer les boîtes de dialogue</a></h2><br><h3 class="Title8 GTitle3">Créer une boîte de dialogue (GDialogFind.h)<br></h3><div class="GCode1"><pre class="Code2"><xmp class="AceCode" data-mode="c_cpp">//===============================================
#ifndef _GDialogFind_
#define _GDialogFind_
//================================================
#include "GInclude.h"
//===============================================
class GDialogFind : public QDialog {
    Q_OBJECT

public:
    GDialogFind(QWidget* parent = 0);
    ~GDialogFind();

public:

private:
    QLabel* m_label;
    QLineEdit* m_lineEdit;
    QCheckBox* m_caseCheckBox;
    QCheckBox* m_backwardCheckBox;
    QPushButton* m_findButton;
    QPushButton* m_closeButton;
};
//===============================================
