//===============================================
void GClient::print(const std::list&lt;int&gt;&amp; _list) {
    for (auto lItem : _list) {
        std::cout &lt;&lt; lItem &lt;&lt; " -&gt; ";
    }
    std::cout &lt;&lt; "NULL" &lt;&lt; std::endl;
}
//===============================================</pre><br><h2 class="GTitle1 Title1">
<a class="Title2" id="operations-sur-les-listes_insertion-d-un-element-a-partir-d-une-position" href="#operations-sur-les-listes">Insertion d'un élément à partir d'une position</a>
</h2><br><pre class="GCode1 Code1 AceCode" data-mode="c_cpp" data-theme="gruvbox" data-bg-color="transparent" style="background-color: transparent;">//===============================================
std::list&lt;int&gt; lList;
std::list&lt;int&gt;::iterator it = getIterator(lList.begin(), 4);
lList.insert(it, 100);
//===============================================
std::list&lt;int&gt;::iterator GClient::getIterator(std::list&lt;int&gt;::iterator it, int x) {
    for(int i = 0; i &lt; x; ++i, ++it);
    return it;
}
//===============================================</pre><br><h2 class="GTitle1 Title1">
<a class="Title2" id="operations-sur-les-listes_verification-d-un-iterateur-de-liste" href="#operations-sur-les-listes">Vérification d'un itérateur de liste</a>
</h2><br><pre class="GCode1 Code1 AceCode" data-mode="c_cpp" data-theme="gruvbox" data-bg-color="transparent" style="background-color: transparent;">//===============================================
std::list&lt;int&gt; lList;
std::list&lt;int&gt;::iterator it = getIterator(lList.begin(), 4);
if(it != lList.end())
    std::cout &lt;&lt; *it;
else
    std::cout &lt;&lt; "not found";
//===============================================
std::list&lt;int&gt;::iterator GClient::getIterator(std::list&lt;int&gt;::iterator it, int x) {
    for(int i = 0; i &lt; x; ++i, ++it);
    return it;
}
//===============================================</pre><br><h2 class="GTitle1 Title1">
<a class="Title2" id="operations-sur-les-listes_recherche-d-un-element-dans-une-liste" href="#operations-sur-les-listes">Recherche d'un élément dans une liste</a>
</h2><br><pre class="GCode1 Code1 AceCode" data-mode="c_cpp" data-theme="gruvbox" data-bg-color="transparent" style="background-color: transparent;">//===============================================
std::list&lt;int&gt; lList;
std::list&lt;int&gt;::iterator it = find (lList.begin(), lList.end(), 15);
if (it != lList.end())
    std::cout &lt;&lt; "Element found in linkedList: " &lt;&lt; *it;
else
    std::cout &lt;&lt; "Element not found in linkedList";
//===============================================</pre><br><h2 class="GTitle1 Title1">
<a class="Title2" id="operations-sur-les-listes_suppression-d-un-element-dans-une-liste" href="#operations-sur-les-listes">Suppression d'un élément dans une liste</a>
